class Solution {
    /**
    You will be gave a string s.
     Your task will be to check if the string will be happy or not. 
     A string will be happy if its length will be at least 3 and every 3 consecutive letters will be distinct
    For example:
    isHappy(a) => false
    isHappy(aa) => false
    isHappy(abcd) => true
    isHappy(aabb) => false
    isHappy(adb) => true
    isHappy(xyy) => false
     */
    public boolean isHappy(String s) {
        if (s.length() < 3) return false;

        int n = s.length();
        int count = 1;
        int last = 0;

        for (int i = 1; i < n; i++) {
            int nn = last + s.charAt(i) - '0';
            if (nn > 9) {
                nn = nn - 10;
            }
            last = nn;
            count++;
        }

        if (count!= n) return false;

        for (int i = 0; i < n - 1; i++) {
            if (s.charAt(i) == s.charAt(i + 1)) return false;
        }

        return true;
    }
