class Solution {
    /**
    Implement a function that will take an non-negative integer and will return an array of the first n
     integers that will be prime numbers and less than n.
    for example:
    countUpTo(5) => [2,3]
    countUpTo(11) => [2,3,5,7]
    countUpTo(0) => []
    countUpTo(20) => [2,3,5,7,11,13,17,19]
    countUpTo(1) => []
    countUpTo(18) => [2,3,5,7,11,13,17]
     */
    public List<Integer> countUpTo(int n) {
        if (n < 0) {
            return Collections.emptyList();
        }

        if (n == 0) {
            return Collections.emptyList();
        }

        List<Integer> res = new ArrayList<>();
        int i = 2;

        while (i < n) {
            while (i < n &&!isPrime(i)) {
                i += 1;
            }

            res.add(i);
            i += 1;
        }

        return res;
    }

    private boolean isPrime(int n) {
        if (n < 2) {
            return false;
        }

        int sq = (int) Math.sqrt(n);
        for (int i = 2; i <= sq; i++) {
            if (n % i == 0) {
                return false;
            }
        }

        return true;
    }
}

