        StringBuilder sb = new StringBuilder();
        int remainder = (n - avg);

        int temp = remainder;

        int i = 0;
        int j = remainder;

        while (temp > 0) {
            sb.append(temp % 2);

            temp = temp / 2;

            i++;
        }

        while (j > 0) {
            sb.append(j % 2);

            j = j / 2;

            j--;
        }

        String s = sb.reverse().toString();

        return s.equals("")? -1 : Integer.parseInt(s, 2);
    }
}

