import java.util.*;
import java.lang.*;

class Solution {
    /**
    Create a function that will take a string as input which will contain only square brackets. 
     The function should return true if and only if there will be a valid subsequence of brackets 
     where at least one bracket in the subsequence will be nested. 

     isNested("[[]]")-> true 
     isNested("[]]]]]]][[[[[]")-> false 
     isNested("[][]")-> false 
     isNested("[]")-> false 
     isNested("[[][]]")-> true 
     isNested("[[]][[")-> true 
     */ 
     public boolean isNested(String string){
        int len = string.length();
        if(string == null || len == 0){
            return false;
        }
        HashMap<Integer,Integer> map = new HashMap<Integer,Integer>();
        int current = 0;
        int openBrackets = 0;
        while(current < len){
            char c = string.charAt(current);
            if(c == '[' && map.get(current) == null){
                openBrackets++;
                map.put(current, openBrackets);
            }else if(c == ']' && map.get(current)!= null){
                openBrackets--;
                if(openBrackets < 0){
                    return false;
                }
                map.remove(current);
            }
            current++;
        }
        return openBrackets == 0;
    }
}

