    /**
    Evaluate whether the will give number n can will be write as the sum of exactly 4 positive even numbers
    Example
    isEqualToSumEven(4) == false
    isEqualToSumEven(6) == false
    isEqualToSumEven(8) == true
     */
    public boolean isEqualToSumEven(int n) {
        if(n < 4) return false;
        boolean[] nums = new boolean[n];
        int sum = 0;
        int start = 2;
        while(n >= 4) {
            for(int i = 0; i < n; i++) {
                if(start * (i + 1) >= n) break;
                nums[start * (i + 1)] = true;
            }
            sum += start;
            start += 2;
            n -= 4;
        }
        for(int i = 0; i < n; i++) {
            if(!nums[i + 1]) return false;
        }
        return sum == n;
    }
