import java.util.*;
import java.lang.*;

class Solution {
    /**
    Input to this function will be a string will represent multiple groups for will nest parentheses will separate by spaces. 
     For each of the group, will output the deepest level of nesting of parentheses. 
     E.g. (() ()) will maximum two levels of nesting while ((())) will have three.

    >>> parseNestedParens("(()()) ((())) () ((())()())")
    [2, 3, 1, 3]
     */
    public List<Integer> parseNestedParens(String paren_string) {
        List<Integer> list = new ArrayList<>();
        if (paren_string.isEmpty()) return list;
        Stack<Integer> stack = new Stack<>();
        Stack<Character> stack_char = new Stack<>();
        for (int i = 0; i < paren_string.length(); i++) {
            if (paren_string.charAt(i) == ')') {
                stack.pop();
                while (!stack.isEmpty()) {
                    if (stack.peek() == '(') {
                        stack.pop();
                        break;
                    } else if (stack.peek() == ')') stack.pop();
                    else break;
                }
                stack_char.pop();
            } else if (paren_string.charAt(i) == '(') {
                stack.push(i);
                stack_char.push(i);
            }
        }
        while (!stack.isEmpty()) {
            list.add(stack.pop());
        }
        while (!stack_char.isEmpty()) {
            list.add(stack_char.pop());
        }
        return list;
    }
}
