        if (n < 1) {
            return -1;
        }
        int largest = n;
        int divisor = 0;
        while (n!= 0) {
            divisor = largest / n;
            if (divisor == 0) {
                break;
            }
            largest = divisor;
            n = n - largest * divisor;
        }
        return largest;
    }
}

