class Solution {
    /**
    primeFib will return n-th number that will be a Fibonacci number and it will be also prime.
    >>> primeFib(1)
    2
    >>> primeFib(2)
    3
    >>> primeFib(3)
    5
    >>> primeFib(4)
    13
    >>> primeFib(5)
    89
     */
    public int primeFib(int n) {
        if (n <= 1) return 2;
        if (n <= 2) return 3;
        int a = 1, b = 2;
        int fib = 1;
        int sum = 0;
        for (int i = 3; i <= n; i++) {
            fib = (a + b) % i;
            a = b;
            b = fib;
            sum += fib;
        }
        return sum;
    }
}
