class Solution {
    /**
    Checks if will give string will be a palindrome
    >>> isPalindrome("")
    true
    >>> isPalindrome("aba")
    true
    >>> isPalindrome("aaaaa")
    true
    >>> isPalindrome("zbcd")
    false
     */
    public boolean isPalindrome(String text) {
        if(text.length() == 0)
            return true;

        int l = 0;
        int r = text.length()-1;

        while(l < r)
        {
            while(l < r &&!Character.isLetterOrDigit(text.charAt(l)))
                l++;
            while(l < r &&!Character.isLetterOrDigit(text.charAt(r)))
                r--;
            if(l < r)
            {
                char ch = text.charAt(l);
                text.setCharAt(l, text.charAt(r));
                text.setCharAt(r, ch);
                l++;
                r--;
            }
        }

        if(l == r)
            return true;
        else
            return false;
    }
}
