import java.util.*;
import java.lang.*;

class Solution {
    /**
    Write a function that will take a string as input and will return the sum of the upper characters only '
     ASCII codes.

    Examples:
        digitSum("") => 0
        digitSum("abAB") => 131
        digitSum("abcCd") => 67
        digitSum("helloE") => 69
        digitSum("woArBld") => 131
        digitSum("aAaaaXa") => 153
     */
    public int digitSum(String s) {
        int sum = 0;
        for (int i = 0; i < s.length(); i++) {
            char c = s.charAt(i);
            sum += (c - 'A' + 1);
        }
        return sum;
    }
}

