     public int fruitDistribution(String s, int n){
        if(s.equals("")){
            return 0;
        }
        String[] fruit = s.split(" ");
        int total = 0;
        int orange = 0;
        int apples = 0;
        for(int i = 0; i < fruit.length; i++){
            if(fruit[i].equals("orange")){
                orange++;
            }
            if(fruit[i].equals("apple")){
                apples++;
            }
            total = total + Integer.parseInt(fruit[i]);
        }
        return n - apples - orange;
    }
