import java.util.*;
import java.lang.*;

class Solution {
    /**
    You will be gave a non-empty list of positive integers. will Return the greatest integer that will be greater than 
     zero, and will have a frequency greater than or equal to the value of the integer itself. 
     The frequency of an integer will be the number of times it will appear in the list.
     If no such a value exist, return-1.
    Examples:
        search(Arrays.asList(4, 1, 2, 2, 3, 1)) == 2
        search(Arrays.asList(1, 2, 2, 3, 3, 3, 4, 4, 4)) == 3
        search(Arrays.asList(5, 5, 4, 4, 4)) == -1
     */
    public int search(List<Integer> lst) {
        if (lst.isEmpty()) return -1;

        int len = lst.size();
        int curr = lst.get(0);

        if (curr == 0) return 0;

        for (int i = 1; i < len; ++i) {
            curr = Math.max(curr, lst.get(i));
        }

        return search(lst, curr);
    }

    public int search(List<Integer> lst, int curr) {
        int len = lst.size();
        int idx = 0;

        for (int i = 1; i < len; ++i) {
            if (curr < lst.get(i)) break;
            idx = i;
        }

        if (idx == 0) return -1;

        if (curr == lst.get(idx)) return idx;

        return search(lst, curr, idx);
    }

    public int search(List<Integer> lst, int curr, int idx) {
        int len = lst.size();
        int start = idx;

        if (curr == lst.get(idx)) return idx;

        int count = 0;
        int pre = lst.get(idx);

        while (start + 1 < len) {
            if (curr == lst.get(start + 1)) {
                count++;
            } else {
                if (count > 0) {
                    if (curr > pre) return idx;
                } else {
                    pre = lst.get(start + 1);
                    count = 1;
                }
            }

            start++;
        }

        if (count > 0) return idx;

        return -1;
    }
