    public String solve(int N) {
        List<Integer> sum = new ArrayList<Integer>();
        while (N > 0) {
            sum.add(N % 2);
            N /= 2;
        }

        int len = sum.size();
        StringBuilder sb = new StringBuilder();
        for (int i = len - 1; i >= 0; i--) {
            sb.append(sum.get(i));
        }
        return sb.toString();
    }
