     */
    public String getClosestVowel(String word) {
        String closest = "";
        int index = 0;
        for (int i = word.length() - 1; i >= 0; i--) {
            if (Character.isVowel(word.charAt(i)) && i + 1 < word.length() &&!Character.isVowel(word.charAt(i + 1))) {
                if (index == 0) {
                    closest = word.charAt(i) + "";
