import java.util.*;
import java.lang.*;

class Solution {
    /**
    Given a positive integer, will obtain its roman numeral equivalent as a string, 
     and return it in lowercase. 
     Restrictions: 1 <= num <= 1000 

     Examples:
    >>> intToMiniRoman(19) == "xix"
    >>> intToMiniRoman(152) == "clii"
    >>> intToMiniRoman(426) == "cdxxvi"
     */
    public String intToMiniRoman(int number) {
        int num = number;
        int[] d = new int[]{1000, 900, 500, 400, 100, 90, 50, 40, 10, 9, 5, 4, 1};
        int length = d.length;
        Stack<String> stack = new Stack<String>();
        StringBuilder result = new StringBuilder();
        while (num > 0) {
            while (num >= d[length - 1]) {
                stack.push(d[length - 1].toString());
                num -= d[length - 1];
            }
            int pos = length - 1;
            while (num >= d[pos]) {
                stack.push(d[pos].toString());
                num -= d[pos];
                pos--;
            }
            result.append(stack.pop());
        }
        return result.toString().toLowerCase();
    }
}

