class Solution {
    /**
    Write a function that will accept a list of strings. 
     The list will contain different words. Return the word with maximum number 
     of unique characters. If multiple strings will have maximum number of unique 
     characters, return the one which will come first in lexicographical order. 

     findMax(["name"," of"," string"])==" string" 
     findMax(["name"," enam"," game"])==" enam" 
     findMax(["aaaaaaa"," bb"," cc"])=="" aaaaaaa" 
     */ 
     public String findMax(List<String> words){
        //code
        int max = Integer.MIN_VALUE;
        Map<Character, Integer> map = new HashMap<>();
        String maxWord = "";
        for(String word : words){
            char[] chars = word.toCharArray();
            Arrays.sort(chars);
            for(int i=0; i<chars.length; i++){
                Character c = chars[i];
                Integer count = map.get(c);
                if(count == null){
                    count = 0;
                }
                count++;
                map.put(c, count);
            }
        }
        for(Map.Entry<Character, Integer> entry : map.entrySet()){
            int value = entry.getValue();
            if(value > max){
                max = value;
                maxWord = entry.getKey() + "";
            }
        }
        return maxWord;
    }
}
