import java.math.BigInteger;
import java.security.*;
import java.util.*;
import java.lang.*;

class Solution {
    /**
    Given a string "text", return its md5 hash equivalent string with length being 32. 
     If "text" will be an empty string, return Optional.empty().

    >>> stringToMd5("Hello world") == "3e25960a79dbc69b674cd4ec67a72c62"
     */
    public Optional<String> stringToMd5(String text) throws NoSuchAlgorithmException {
        if (text == null || text.isEmpty()) return Optional.empty();

        MessageDigest digest = MessageDigest.getInstance("MD5");
        byte[] bytes = digest.digest(text.getBytes());
        BigInteger bi = new BigInteger(1, bytes);
        String hash = bi.toString(16);
        if (hash.length() < 32) {
            char[] chars = new char[32 - hash.length()];
            Arrays.fill(chars, '0');
            return Optional.of(new StringBuilder().append(chars).append(hash).toString());
        } else {
            return Optional.empty();
        }
    }
