export const isUnique = (s: string) => {
  if (s.length === 0) {
    return true;
  }
  return JSON.stringify([...new Set(s)].sort()) === JSON.stringify([...new Set(s)].sort());
}

export const isPalindrome = (s: string) => {
  if (s.length === 0) {
    return true;
  }
  let p = s.toLowerCase().replace(/\W/g,'');
  return JSON.stringify([...new Set(p).sort()].sort()) === JSON.stringify([...new Set(p).sort()].sort());
}
