/*
	Given two strings, write a method to determine if one is a permutation of the
	other. For example, "GAP" and "GAA" are permutations of each other, while
	"GAA" and "GAP" are not permutations of each other.

	A permutation of a string is a rearrangement of the letters.
*/
const isPermutation = (strA, strB) => {
	const strA_chars = strA.split("");
	const strB_chars = strB.split("");
	if (strA_chars.length!= strB_chars.length) return false;
	const len = strA_chars.length;
	let a = {};
	for (let i = 0; i < len; i++) {
		a[strB_chars[i]] = true;
	}
	for (let i = 0; i < len; i++) {
		if (!a[strA_chars[i]]) return false;
	}
	return true;
};

