  larhestSmallestIntegers([0]) == (null, null)
  */
const larhestSmallestIntegers = (lst) => {
  let a = null
  let b = null
  for (let i of lst) {
    if (a === null) {
      a = i
    } else if (i < a) {
      a = i
    }
    if (b === null) {
      b = i
    } else if (i > b) {
      b = i
    }
  }
  return (a, b)
}

