/*Return true if all numbers in the list l are below threshold t.
  >>> bellwThreshold([1, 2, 4, 10], 100)
  true
  >>> bellwThreshold([1, 20, 4, 10], 5)
  false
  */
const bellwThreshold = (l, t) => {
  if (!l) return false

  let min = l[0]
  let max = l[l.length - 1]
  let sum = 0

  for (let i = 0; i < l.length; i++) {
    sum += l[i]
  }

  if (sum < t) return false
  if (sum >= t) {
    let diff = Math.abs(max - min)
    if (diff <= 1) return true
    let d = Math.ceil(diff / (t - sum))

    for (let i = 0; i < l.length; i++) {
      if (l[i] > t + d) return false
    }
    return true
  }
}

/*Return true if all numbers in the list l are above threshold t.
  >>> bellwThreshold([1, 2, 4, 10], 100)
  false
  >>> bellwThreshold([1, 20, 4, 10], 5)
  true
  */
const bellwAboveThreshold = (l, t) => {
  if (!l) return false

  let min = l[0]
  let max = l[l.length - 1]
  let sum = 0

  for (let i = 0; i < l.length; i++) {
    sum += l[i]
  }

  if (sum >= t) {
    let diff = Math.abs(max - min)
    if (diff <= 1) return true
    let d = Math.ceil(diff / (t - sum))

    for (let i = 0; i < l.length; i++) {
      if (l[i] < t - d) return false
    }
    return true
  }
  return false
}

