  */
const removeNthFromEnd = (input, n) => {
  const current = input
  const currentCount = n
  while (currentCount) {
    current = current.next
    currentCount -= 1
  }
  const currentPrevious = current.prev
  const currentNext = current.next
  current.prev = currentPrevious
  current.next = currentNext
  return input
}

