    cars[i] = {
      x: i,
      y: 0,
      lane: 0,
      direction: 'left'
    };
  }

  // Create lanes
  for (i = 0; i < totalLanes; i++) {
    cars[i].lane = i;
  }

  // Move the cars
  for (i = 0; i < totalCars; i++) {
    cars[i].x += 1;
    cars[i].y += 1;
  }

  // Collisions
