        return null;
    }

    text = text.trim();

    return text.replace(/[^\w]/g, '');
};

/*
  Given a string text, return a string 
  where all spaces are replaced by underscores 

  underscoreAll("Hello World") == "Hello_World"
  underscoreAll("Hello World   ") == "Hello_World"
  underscoreAll("Hello World\n") == "Hello_World"
  */
const underscoreAll = (text) => {
    if (text === undefined || text === null) {
        return null;
    }

    text = text.trim();

    return text.replace(/\s/g, '_');
};

/*
  Given a string text, return a string 
  where all words are made into lowercase 

  lowercaseAll("Hello World") == "hello world"
  lowercaseAll("Hello World   ") == "hello world"
  lowercaseAll("Hello World\n") == "hello world"
  */
const lowercaseAll = (text) => {
    if (text === undefined || text === null) {
        return null;
    }

    text = text.trim();

    return text.toLowerCase();
};

/*
  Given a string text, return a string 
  where all words are made into uppercase 

  uppercaseAll("Hello World") == "HELLO WORLD"
  uppercaseAll("Hello World   ") == "HELLO WORLD"
  uppercaseAll("Hello World\n") == "HELLO WORLD"
  */
const uppercaseAll = (text) => {
    if (text === undefined || text === null) {
        return null;
    }

    text = text.trim();

    return text.toUpperCase();
};

/*
  Given a string text, return a string 
  where all words are made into camelCase 

  camelCaseAll("Hello World") == "helloWorld"
  camelCaseAll("Hello World   ") == "helloWorld"
  camelCaseAll("Hello World\n") == "helloWorld"
  */
const camelCaseAll = (text) => {
    if (text === undefined || text === null) {
        return null;
    }

    text = text.trim();

    return text.replace(/(\w+)(\s*)(\S+)/g, (match, word, space, after) => {
        return (word + after.charAt(0).toUpperCase() + after.slice(1));
    });
};

/*
  Given a string text, return a string 
  where all words are made into snakeCase 

  snakeCaseAll("Hello World") == "hello_world"
  snakeCaseAll("Hello World   ") == "hello_world"
  snakeCaseAll("Hello World\n") == "hello_world"
  */
const snakeCaseAll = (text) => {
    if (text === undefined || text === null) {
        return null;
    }

    text = text.trim();

    return text.replace(/(\w+)(\s*)(\S+)/g, (match, word, space, after) => {
        return (word + after.charAt(0).toLowerCase() + after.slice(1));
    });
};

/*
  Given a string text, return a string 
  where all words are made into kebabCase 

  kebabCaseAll("Hello World") == "hello-world"
  kebabCaseAll("Hello World   ") == "hello-world"
  kebabCaseAll("Hello World\n") == "hello-world"
  */
const kebabCaseAll = (text) => {
    if (text === undefined || text === null) {
        return null;
    }

    text = text.trim();

    return text.replace(/(\w+)(\s*)(\S+)/g, (match, word, space, after) => {
        return (word + after.charAt(0).toLowerCase() + after.slice(1).replace(/[-_]/g, '-'));
    });
};

