            if (car.position === otherIndex) {
                car.colliding = true;
                collisions++;
            }

            if (car.direction === otherCar.direction) {
                if (car.speed < otherCar.speed) {
                    car.position = otherIndex;
                } else {
                    otherCar.position = index;
                }

                car.colliding = otherCar.colliding;
                otherCar.colliding = car.colliding;
                collisions++;
            }
        });
    });

