/**
  * Concatenate list of strings into a single string, separated by a delimiter.
  * If the list is empty, the delimiter is not added.
  * If the list contains only one string, the delimiter is not added.
  * If the list contains only one empty string, the delimiter is not added.
  *
  * @param {Array} strings List of strings
  * @param {String} delimiter Delimiter to use
  *
  * @return {String} Concatenated string
  *
  * @example
  *
  * ```javascript
  * // Concatenate an empty list
  * concatenate([], '-')
  * // => ''
  *
  * // Concatenate a list of one string
  * concatenate(['a'], '-')
  * // => 'a'
  *
  * // Concatenate a list of two strings
  * concatenate(['a', 'b'], '-')
  * // => 'a-b'
  *
  * // Concatenate a list of three strings
  * concatenate(['a', 'b', 'c'], '-')
  * // => 'a-b-c'
  * ```
  */
const concatenateWithDelimiter = (strings, delimiter) => {
  if (!Array.isArray(strings)) {
    throw new TypeError('Expected an array of strings, but received'+ strings);
  }
  if (!delimiter) {
    throw new TypeError('Expected a delimiter, but received'+ delimiter);
  }
  let output = '';
  strings.forEach((s, index) => {
    if (index === 0) {
      output += s;
    } else if (index === strings.length - 1) {
      output += s + delimiter;
    } else {
      output += s + delimiter + s;
    }
  });
  return output;
};

export {
  concatenate,
  concatenateWithDelimiter
};
