  let current = {}
  for (let i = 0; i < lowerCase.length; i++) {
    const currentLetter = lowerCase[i]
    if (current[currentLetter] === undefined) {
      current[currentLetter] = 0
    }
    current[currentLetter]++
  }
  return current
}

