/*
  Given two strings s1 and s2, return a string consisting
  of the characters that appear in both s1 and s2.
  Example:
  >>> commonChars('a, b, c', 'b')
  'b'
  >>> commonChars('a', 'b, c')
  'b, c'
*/
const commonChars = (s1, s2) => {
  let ret = ''
  for (let i = 0; i < s1.length; i++) {
    if (s2.indexOf(s1[i]) >= 0) {
      ret += s1[i]
    }
  }
  return ret
}

