  If the string contains no letters, reverse the string.
  The function should return the resulted string.
  Examples
  solve("1234") = "4321"
  solve("ab") = "AB"
  solve("#a@C") = "#A@c"
  */
const solve = (s) => {
  const len = s.length
  let r = s
  if (len > 1) {
    for (let i = 0; i < len / 2; i++) {
      r = r.split('').reverse().join('')
    }
  }
  return r
}

