const convertToBracketStack = (input) => {
  const stack = []
  let current = ""
  for (let i = 0; i < input.length; i += 1) {
    if (input[i] === '<') {
      current = ">"
    } else {
      current = ""
    }
    stack.push(current)
  }
  return stack
}

const findLongestSequence = (stack, result = []) => {
  // if there are no brackets, return false
  if (!stack.length) {
    return false
  }

  // get the last item in the stack
  const lastItem = stack[stack.length - 1]

  // if the last item is the same as the last item of the previous result, return the previous result
  if (lastItem === result[result.length - 1]) {
    return result
  }

  // otherwise, try to match a matching pair
  let matchingPair = null
  stack.forEach(char => {
    if (char === lastItem) {
      matchingPair = char
    }
  })

  // if there is a matching pair, then try to find the longest matching sequence
  if (matchingPair) {
    const newResult = findLongestSequence(stack.slice(0, -1), result.concat(matchingPair))
    if (newResult) {
      return newResult
    }
  }

  // otherwise, there is no matching pair, so return false
  return false
}

