  // Find the longest postfix of string that is a palindrome.
  // This is the prefix that we will append to the string to get a palindrome.
  while (isPalindrome(string + prefix) === false) {
    postfix = postfix + string.slice(0, -1);
    string = string.slice(1);
  }
  prefix = string.slice(0, -1);

  // Append the reversed prefix to the end of the supplied string.
  return prefix + string + postfix;
}

