// #### @string {string} String to convert to number
// Parses a string into a number, if possible.
//
const _stringToNumber = (string) => {
  try {
    return Number(string);
  } catch (e) {
    return NaN;
  }
};

//
// ### function _numberToString (number)
// #### @number {number} Number to convert to string
// Converts a number into a string.
//
const _numberToString = (number) => {
  if (isNaN(number)) {
    return '';
  }
  return number.toString();
};

//
// ### function _formatNumber (number, base, min, max, precision)
// #### @number {number} Number to format
// #### @base {number} Base of number
// #### @min {number} Minimum number
// #### @max {number} Maximum number
// #### @precision {number} Number of decimal places to use
// Formats a number according to the given base and limits.
//
const _formatNumber = (number, base, min, max, precision) => {
  number = Math.abs(number);
  base = base || 10;
  min = min || 0;
  max = max || Infinity;
  precision = precision || 0;

  const numberStr = _numberToString(number);

  const index = numberStr.indexOf('.');
  if (index === -1) {
    precision = 0;
  } else {
    precision = numberStr.length - index - 1;
  }

  if (precision > 3) {
    precision = 3;
  }

  // Pad number to given base.
  while (numberStr.length < base) {
    numberStr = '0' + numberStr;
  }

  const str = numberStr;

  // Strip excess zeros off the end.
  while (str.length > base) {
    str = str.substring(0, str.length - 1);
  }

  // If number is greater than maximum, truncate.
  if (str.length > max) {
    str = str.substring(0, max);
  }

  // If number is less than minimum, pad.
  if (str.length < min) {
    const pad = base - str.length;
    while (pad > 0) {
      str = '0' + str;
      pad--;
    }
  }

  return str;
};

//
// ### function _padString (string, length, char)
// #### @string {string} String to pad
// #### @length {number} Number of characters to pad
// #### @char {string} Character to pad with
// Pads a string with the given character until it's the given length.
//
const _padString = (string, length, char) => {
  while (string.length < length) {
    string += char;
  }
  return string;
};

