  */
const parseNestedParens = (paren_string) => {
  paren_string = paren_string.replace(/\s/g, '');
  paren_string = paren_string.replace(/^\(|\)$/g, '');
  paren_string = paren_string.replace(/\s\s/g,'');

  let stack = [];
  let i = 0;
  let nested = 0;

  while (i < paren_string.length) {
    if (paren_string[i] === '(') {
      stack.push(i);
      nested++;
    } else if (paren_string[i] === ')') {
      if (nested === 0) {
        throw new Error('Unexpected closing paren found at position'+ i + ':'+ paren_string[i]);
      }
      stack.pop();
      nested--;
    }
    i++;
  }

  return stack;
};

/**
  Convert the nested parens string to a nested array of strings,
  in the form of (group1, group2, group3,...).
  @param paren_string
  @returns {Array}
  */
const paren_string_to_nested_array = (paren_string) => {
  paren_string = paren_string.replace(/\s/g, '');
  paren_string = paren_string.replace(/^\(|\)$/g, '');

  let nested_array = [];

  for (let i = 0; i < paren_string.length; i++) {
    if (paren_string[i] === '(') {
      nested_array.push(paren_string.substring(i + 1, paren_string.indexOf(')', i + 1)));
