  const result = words.reduce((previous, current) => {
    if (uniqueChars.has(current)) {
      return previous + current;
    } else {
      uniqueChars.add(current);
      return previous + current;
    }
  });

  if (uniqueChars.size === 0) {
    return "";
  }

  return words.find((word) => uniqueChars.has(word));
};

