  vowels("Hello world!")  3
  vowels("Hello,world!")  1
  vowels("abcdef")  1
  */
const vowels = (txt) => {
  let t = splitWords(txt)
  let l = t.length
  let odd = 0
  let word
  while (odd < l) {
    word = t[odd]
    if (isVowel(word)) {
      odd++
      }
    }
  return odd
}

/* Given a string of words, return the number of vowels with odd order in the alphabet
  Examples
  vowels("Hello world!")  3
  vowels("Hello,world!")  1
  vowels("abcdef")  2
  */
const allWords = (txt) => {
  let t = splitWords(txt)
  let l = t.length
  let odd = 0
  let word
  while (odd < l) {
    word = t[odd]
    if (isLowerCase(word) || isUpperCase(word) || isDigit(word) || isVowel(word)) {
      odd++
      }
    }
  return odd
}

const isLowerCase = (txt) => {
  return txt.toLowerCase() == txt
}

const isUpperCase = (txt) => {
  return txt.toUpperCase() == txt
}

const isDigit = (txt) => {
  return txt.match(/^[0-9]$/)
}

const isVowel = (txt) => {
  return txt.match(/[aeiouAEIOU]/)
}

const isConsonant = (txt) => {
  return txt.match(/[bcdfghjklmnpqrstvwxyzBCDFGHJKLMNPQRSTVWXYZ]/)
}

export {
  splitWords,
  oddWords,
  upperWords,
  digits,
  consonants,
  vowels,
  allWords
}
