  >>> sumOfPrimes(1000)
  15000
  >>> sumOfPrimes(10000)
  150000
  >>> sumOfPrimes(100000)
  200000
  */
const sumOfPrimeFractions =(n) => {
  var sum = 0;
  for (var i = 2; i <= n; i++) {
    if (primeFactorize(i).length === 0) {
      var primes = primeFactorize(i);
      var j = primes.length - 1;
      while (j >= 0) {
        sum += primes[j];
        j -= 1;
      }
