  permutations(words) -> [['foo', 'bar', 'baz'], ['foo', 'baz', 'bar'], ['bar', 'foo', 'baz'], ['bar', 'baz', 'foo'], ['baz', 'foo', 'bar'], ['baz', 'bar', 'foo']]

  */
const permutations = (words) => {
  var result = [];
  const all_permutations = _.shuffle(words);

  for (const permutation of all_permutations) {
    result.push(permutation);
  }
  return result;
}

/*
  Given a list of words, return the list of unique words.
  e.g. words = ['foo', 'bar', 'baz']
  unique_words(words) -> ['foo', 'bar', 'baz']

  */
const unique_words = (words) => {
  return new Set(words).values();
}

