 * Function to convert number base to decimal.
 *
 * @param  {number} input number
 * @param  {number} base to convert to
 * @return {number}
 */
const convertBase = (x, base) => {
  var res = 0;
  while (x > 0) {
    res = res * base + x % base;
    x = Math.floor(x / base);
  }
  return res;
};

/**
 * Function to convert number to base 10.
 *
 * @param  {number} input number
 * @return {number}
 */
const toDecimal =(x) => {
  var res = 0;
  while (x > 0) {
    res = res * 10 + x % 10;
    x = Math.floor(x / 10);
  }
  return res;
};

/**
 * Function to convert number to base 2.
 *
 * @param  {number} input number
 * @return {number}
 */
const toBinary =(x) => {
  var res = 0;
  while (x > 0) {
    res = res * 2 + x % 2;
    x = Math.floor(x / 2);
  }
  return res;
};

/**
 * Function to convert number to base 8.
 *
 * @param  {number} input number
 * @return {number}
 */
const toOctal =(x) => {
  var res = 0;
  while (x > 0) {
    res = res * 8 + x % 8;
    x = Math.floor(x / 8);
  }
  return res;
};

/**
