const parseNestedParens = (s) => {
  s = s.replace(/\s+/g, " ");
  let stack = [];
  let level = 0;
  for (let i = 0; i < s.length; i++) {
    if (s[i] === ')') {
      level -= 1;
      stack.pop();
    } else if (s[i] === '(') {
      level += 1;
      stack.push(level);
    }
  }
  if (stack.length === 0) {
    return 0;
  } else {
    return stack.length;
  }
}
