/*
  For two positive integers a and b, return the even digits between a and b in ascending order.

  For example:
  generateIntegers(2, 8) => [2, 4, 6, 8]
  generateIntegers(8, 2) => [2, 4, 6, 8]
  generateIntegers(10, 14) => []
  */
const generateIntegers = (a, b) => {
  return a > b
   ? a > 9? [] : [...generateIntegers(a - 1, 9)]
    : b > 9? [] : [...generateIntegers(9, b - 1)]
}

