const selectWords = (s, n) => {
  const charArr = s.split('')
  const i = n - 1
  const j = charArr.length - 1
  const wordList = []
  const stack = []
  const lastChar = charArr[j]
  while (i >= 0) {
    if (charArr[i] === lastChar) {
      stack.push(charArr[i])
    } else if (stack.length === n) {
      wordList.push(stack)
      stack = []
    } else {
      stack.push(charArr[i])
    }
    i--
  }
  return wordList
}

