const longestCommonSubstring = (a, b) => {
  if (a.length === 0 || b.length === 0) {
    return '';
  }
  let [aStart, aEnd] = [0, a.length];
  let [bStart, bEnd] = [0, b.length];
  for (let i = 0; i < a.length; i++) {
    for (let j = 0; j < b.length; j++) {
      if (a[i] === b[j]) {
        if (i === aStart) {
          aStart = j;
        }
        if (j === bStart) {
          bStart = i;
        }
        if (i === aEnd) {
          aEnd = j;
        }
        if (j === bEnd) {
          bEnd = i;
        }
      } else {
        break;
      }
    }
    if (aEnd - aStart > bEnd - bStart) {
      [aStart, aEnd] = [bStart, bEnd];
    } else {
      [bStart, bEnd] = [aStart, aEnd];
    }
  }
  return a.slice(aStart, aEnd);
};

module.exports = {
  allPrefixes,
  longestCommonSubstring
};
