const makeReducer = (state = initialState, actions) => {
  const { type, payload } = actions
  switch (type) {
    case `INC`:
      return {
       ...state,
        numbers: [...state.numbers, payload.numbers[0]]
      }
    case `DEC`:
      return {
       ...state,
        numbers: [...state.numbers, payload.numbers[1]]
      }
    case `RESET`:
      return {
       ...state,
        numbers: [...state.numbers]
      }
    default:
      return state
  }
}

