  */
const startsOneEnds = (n) => {
  let res = 0
  for (let i = 1; i <= n; i++) {
    let t = Math.pow(10, i - 1)
    let oneEnd = n - i + 1
    let twoEnd = n - i + 2
    if (t === 1) {
      res += oneEnd
    } else if (t === 2) {
      res += twoEnd
    } else {
