        return true;
    }
    let currentChar = text[0];
    let reversed = text.split('').reverse().join('');
    let reverseIndex = reversed.length - 1;
    for (let i = 0; i < text.length - 1; i++) {
        if (text[i]!== reversed[reverseIndex]) {
