/* 
  Given an array of numbers, find the highest and lowest number in the array.
  >>> findExtreme( [1, 2, 3, 4, 5, 6, 7] )
  {low:7,high:6}
  >>> findExtreme( [1, 2, 3, 4, 5, 6] )
  {low:1,high:6}
  */
const findExtreme = (numbers) => {
  const len = numbers.length
  let low = numbers[0]
  let high = numbers[0]
  for (let i = 1; i < len; i++) {
    if (numbers[i] < low) {
      low = numbers[i]
    }
    if (numbers[i] > high) {
      high = numbers[i]
    }
  }
  return {low, high}
}

/*
  Given an array of numbers, find the two highest and two lowest numbers.
  >>> findExtreme2( [1, 2, 3, 4, 5, 6, 7] )
  [{low:7,high:6},{low:1,high:6}]
  >>> findExtreme2( [1, 2, 3, 4, 5, 6] )
  [{low:1,high:6},{low:2,high:5}]
  */
const findExtreme2 = (numbers) => {
  const len = numbers.length
  let low = numbers[0]
  let high = numbers[0]
  for (let i = 1; i < len; i++) {
    if (numbers[i] < low) {
      low = numbers[i]
    }
    if (numbers[i] > high) {
      high = numbers[i]
    }
  }
  for (let i = len - 1; i >= 0; i--) {
    if (numbers[i] < low) {
      low = numbers[i]
    }
    if (numbers[i] > high) {
      high = numbers[i]
    }
  }
  return [{low, high}, {low, high}]
}

