  */
const generateIntegers = (a, b) => {
  const min = Math.min(a, b);
  const max = Math.max(a, b);
  const x = (max - min) / 2;
  const start = min + x;
  const end = min + (x + 1);

  return [...Array(end - start),...Array(start, end)].map((_, index) => index + start);
};

