   than the third side.
  Example:
  triangleArea(3, 4, 5) == 6.00
  triangleArea(1, 2, 10) == -1
  */
const triangleArea = (a, b, c) => {
  if (a <= 0 || b <= 0 || c <= 0) {
    return -1
  }
  const area = Math.pow(a, 2) + Math.pow(b, 2) - Math.pow(c, 2)
  return Math.round(area * 10) / 10
}

const isValid = (a, b, c) => {
  return triangleArea(a, b, c) >= 0
}

const getMaxSide = (triangle) => {
  const maxSide = Math.max(triangle[0], triangle[1], triangle[2])
  return maxSide
}

const findTriangle = (triangle) => {
  const [a, b, c] = triangle
  if (!isValid(a, b, c)) {
    return undefined
  }
  const sideSum = (a + b + c)
  const maxSide = getMaxSide(triangle)
  if (sideSum > maxSide) {
    return undefined
  }
  return triangle
}

