   Valid option are' zero',' one',' two',' three',' four',' five',' six',' seven',' eight' and' nine'. 
   revert the string with numbers sorted from smallest to largest
  >>> sortNumbers('three one five')
  'one three five'
  */
const sortNumbers = (numbers) => {
  let input = '',
    numbers = '',
    number = '',
    newNumbers = '';

  // if numbers.length > 0
  if (numbers.length > 0) {
    for (let i = 0; i < numbers.length; i++) {
      number = numbers[i];

      // if number is a number
      if (isNaN(parseInt(number))) {
        newNumbers = newNumbers + number;
      }
      // if number is a character
      else {
        // append character to the string
        input = input + number;
      }
    }

    // if input is not empty
    if (input.length > 0) {
      // split string to get the numberals
      let numbersArray = input.split('');

      // if the input contains more than 1 numberal
      if (numbersArray.length > 1) {
        // get the smallest numberal in the array
        let smallestNumber = numbersArray[0];

        // reverse the smallest numberal in the array
        let smallestNumberReverse = smallestNumber.split('').reverse().join('');

        // add the reversed numberal to the new string
        newNumbers = newNumbers + smallestNumberReverse;

        // remove the smallest numberal from the string
        input = input.replace(smallestNumber, '');

        // split the remaining string to get the numberals
        numbersArray = input.split('');

        // if the remaining string contains more than 1 numberal
        if (numbersArray.length > 1) {
          // reverse the remaining numberals in the array
          let remainingNumbersArray = numbersArray.reverse();

          // add the reversed numberals to the new string
          newNumbers = newNumbers + remainingNumbersArray.join('');
        }

        // add the remaining numberals to the new string
        newNumbers = newNumbers + numbersArray.join('');
      }
      // if the input contains only 1 numberal
      else {
        // add the remaining numberals to the new string
        newNumbers = newNumbers + input;
      }
    }
  }

  return newNumbers;
};

