  >>> hasCloseElements([1.0, 2.8, 3.0, 4.0, 5.0, 2.0], 0.3)
  true
  */
const hasCloseElements = (numbers, threshold) => {
  const elements = Array.from(numbers);
  const elementsLength = elements.length;
  const isClose = (number1, number2) => Math.abs(number1 - number2) <= threshold;
  const allClose = (number1, number2) => number1 === number2;
  const indexOfClosest = (elements, number) => elements.findIndex(isClose(number, element));
  const indexOfClosestAll = (elements, number) => elements.findIndex(allClose(number, element));
  const indexes = elements.map((element, index) => indexOfClosest(elements, element));
  return indexes.every(index => index === indexOfClosestAll(elements, number));
};

const getRandomInt = (min, max) => Math.floor(Math.random() * (max - min + 1)) + min;

const generateNumberedList = (numberOfElements) => {
  const numbers = Array(numberOfElements);
  for (let i = 0; i < numberOfElements; i++) {
    numbers[i] = getRandomInt(1, numberOfElements);
  }
  return numbers;
};

const generateCluster = (numberOfElements, clusterSize) => {
  const clusters = generateNumberedList(clusterSize);
  const clusteredElements = clusters.map(() => generateNumberedList(numberOfElements));
  return clusters.concat(clusteredElements);
};

const generateClusters = (numberOfElements, clustersNumber) => {
  const clusters = generateNumberedList(clustersNumber);
  const clusteredElements = clusters.map(() => generateCluster(numberOfElements, clustersNumber));
  return clusters.concat(clusteredElements);
};

const generateRandomClusters = (numberOfElements, clustersNumber) => {
  const clusters = generateClusters(numberOfElements, clustersNumber);
  const clusteredElements = clusters.map(() => generateCluster(numberOfElements, clustersNumber));
  return clusters.concat(clusteredElements);
};

const getClusterDifference = (cluster1, cluster2) => {
  const elements1 = Array.from(cluster1);
  const elements2 = Array.from(cluster2);
  const elementsDifference = elements1.filter(element =>!elements2.includes(element));
  return elementsDifference;
};

const getClusterElements = (cluster) => Array.from(cluster);

const getClusterSizes = (clusters) => {
  const clustersSizes = clusters.map(getClusterElements);
  const clustersSizesSum = clustersSizes.reduce((sum, elements) => sum + elements.length, 0);
  return clustersSizesSum;
};

const getClusterNumber = (clusters) => {
  const clustersSizes = clusters.map(getClusterSizes);
  return clustersSizes.reduce((sum, elements) => sum + elements, 0);
};

const getClusterPercentage = (clusters) => {
  const clustersSizes = clusters.map(getClusterSizes);
  return clustersSizes.reduce((sum, elements) => sum + elements * 100 / clusters.length, 0);
};

const getClusterVariance = (clusters) => {
  const clustersSizes = clusters.map(getClusterSizes);
  const total = clustersSizes.reduce((sum, elements) => sum + elements, 0);
  const variance = clustersSizes.reduce((sum, elements) => sum + Math.pow(elements - total / clusters.length, 2), 0);
  return variance;
};

const getClustersDifference = (clusters1, clusters2) => {
  const clustersDifference = clusters1.filter(cluster1 =>!clusters2.includes(cluster1));
  return clustersDifference;
};

const getClusterDifferencePercentage = (cluster1, cluster2) => {
  const elements1 = Array.from(cluster1);
  const elements2 = Array.from(cluster2);
  const elementsDifference = elements1.filter(element =>!elements2.includes(element));
  return elementsDifference.length * 100 / elements1.length;
};

const getClusterDifferenceVariance = (cluster1, cluster2) => {
  const elements1 = Array.from(cluster1);
  const elements2 = Array.from(cluster2);
  const elementsDifference = elements1.filter(element =>!elements2.includes(element));
  const total = elements1.length + elements2.length;
  const variance = elementsDifference.length * Math.pow(elementsDifference.length - total, 2) / total;
  return variance;
};

